import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ cartItems, isLoggedIn, setIsLoggedIn }) => {
  const [isProductsOpen, setIsProductsOpen] = useState(false);
  const [hideNav, setHideNav] = useState(false);
  const navigate = useNavigate();

  React.useEffect(() => {
    let lastScroll = window.scrollY;
    const handleScroll = () => {
      const currentScroll = window.scrollY;
      if (currentScroll > lastScroll && currentScroll > 60) {
        setHideNav(true);
      } else {
        setHideNav(false);
      }
      lastScroll = currentScroll;
      setIsProductsOpen(false); // Also close dropdown on scroll
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLogout = () => {
    setIsLoggedIn(false);
    navigate('/');
  };

  const productCategories = [
    { name: 'Currency Notes', path: '/products/currency-notes', icon: 'üíµ' },
    { name: 'Framed Notes', path: '/products/framed-notes', icon: 'üñºÔ∏è' },
    { name: 'Resin Frames', path: '/products/resin-frames', icon: '‚ú®' },
    { name: 'Zodiac Coins', path: '/products/zodiac-coins', icon: 'ü™ô' }
  ];

  return (
    <nav className={`navbar${hideNav ? ' hide' : ''}`}>
      <div className="container">
        <div className="nav-content">
          <Link to="/" className="nav-logo">
  <img src="/assets/website_images/logo.png" alt="uniqueseries logo" style={{height: '48px', width: 'auto', display: 'block'}} />
</Link>

          <div className="nav-links">
            <Link to="/" className="nav-link">Home</Link>

            <div 
  className="products-dropdown"
>
  <span 
    className="nav-link products-trigger"
    onClick={() => setIsProductsOpen((prev) => !prev)}
    tabIndex={0}
    onBlur={() => setTimeout(() => setIsProductsOpen(false), 150)}
    aria-haspopup="true"
    aria-expanded={isProductsOpen}
    role="button"
  >
    Products ‚ñº
  </span>

  {isProductsOpen && (
    <div 
      className="dropdown-menu"
      onClick={() => setIsProductsOpen(false)}
    >
                  {productCategories.map((category, index) => (
                    <Link
                      key={index}
                      to={category.path}
                      className="dropdown-item"
                      onClick={() => setIsProductsOpen(false)}
                    >
                      <span className="dropdown-icon">{category.icon}</span>
                      <span>{category.name}</span>
                    </Link>
                  ))}
                </div>
              )}
            </div>

            <Link to="/about" className="nav-link">About</Link>
            <Link to="/contact" className="nav-link">Contact</Link>
          </div>

          <div className="nav-actions">

            {isLoggedIn ? (
              <button onClick={handleLogout} className="nav-link logout-btn">
                Logout
              </button>
            ) : (
              <Link to="/login" className="nav-link">Login</Link>
            )}

            <Link to="/cart" className="cart-link">
              <div className="cart-icon">
                üõí
                {cartItems.length > 0 && (
                  <span className="cart-count">{cartItems.length}</span>
                )}
              </div>
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
