import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import api from '../services/api';
import { useAuth } from './AuthContext';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [cartTotals, setCartTotals] = useState({
    subtotal: 0,
    gst: 0,
    shipping: 0,
    total: 0
  });
  const [loading, setLoading] = useState(false);
  const { isLoggedIn } = useAuth();

  const fetchCart = useCallback(async () => {
    if (!isLoggedIn) return;
    setLoading(true);
    try {
      const { data } = await api.get('/cart');
      setCartItems(data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    } finally {
      setLoading(false);
    }
  }, [isLoggedIn]);

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  useEffect(() => {
    calculateCartTotals();
  }, [cartItems]);

  const calculateCartTotals = () => {
    if (cartItems.length === 0) {
        setCartTotals({ subtotal: 0, gst: 0, shipping: 0, total: 0 });
        return;
    }
    const subtotal = cartItems.reduce((sum, item) => sum + (item.price * (item.quantity || 1)), 0);
    const gst = 0; // No GST
    const shipping = subtotal > 999 ? 0 : 68;
    const total = subtotal + shipping;

    setCartTotals({
      subtotal,
      gst,
      shipping,
      total
    });
  };

  const addToCart = async (product, quantity = 1, customization = {}) => {
    try {
      await api.post('/cart', { productName: product.name, quantity, customization });
      fetchCart();
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const removeFromCart = async (cartItemId) => {
    try {
      await api.delete(`/cart/${cartItemId}`);
      fetchCart();
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const updateQuantity = async (cartItemId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(cartItemId);
      return;
    }
    try {
      await api.put(`/cart/${cartItemId}`, { quantity: newQuantity });
      fetchCart();
    } catch (error) {
      console.error('Error updating quantity:', error);
    }
  };

  const clearCart = async () => {
    try {
      await api.delete('/cart');
      fetchCart();
    } catch (error) {
      console.error('Error clearing cart:', error);
    }
  };

  const getCartItemsCount = () => {
    return cartItems.reduce((total, item) => total + (item.quantity || 1), 0);
  };

  const checkStockAvailability = async () => {
    return { 
      allItemsAvailable: true, 
      items: cartItems.map(item => ({
        productId: item.id,
        available: true,
        requestedQuantity: item.quantity || 1,
        availableQuantity: 999
      }))
    };
  };

  const value = {
    cartItems,
    cartTotals,
    loading,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartItemsCount,
    checkStockAvailability,
    calculateCartTotals
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
