import React, { useState } from 'react';
import './ProductPages.css';

const ResinFrames = ({ addToCart }) => {
  const [selectedSizes, setSelectedSizes] = useState({});

  const resinFrames = [
    {
      id: 1,
      name: 'Small Resin Frame',
      description: 'Crystal clear resin frame with currency note (small)',
      price: 599,
      image: '/assets/resin_frames/resin-small.jpg'
    },
    {
      id: 2,
      name: 'Large Resin Frame',
      description: 'Crystal clear resin frame with currency note (large)',
      price: 799,
      image: '/assets/resin_frames/resin-big.jpg'
    }
  ];

  const handleSizeSelect = (frameId, sizeIndex) => {
    setSelectedSizes({
      ...selectedSizes,
      [frameId]: sizeIndex
    });
  };

  const handleAddToCart = (frame) => {
    const selectedSizeIndex = selectedSizes[frame.id] || 0;
    const selectedSize = frame.sizes[selectedSizeIndex];

    addToCart({
      name: `${frame.name} - ${selectedSize.size}`,
      price: selectedSize.price,
      description: frame.description,
      category: 'Resin Frames'
    });
    alert(`${frame.name} added to cart!`);
  };

  return (
    <div className="product-page">
      <div className="container">
        <div className="page-header">
          <h1 className="page-title">Resin Frames</h1>
          <p className="page-subtitle">
            Premium resin frames for elegant display of your special currency notes with modern aesthetics
          </p>
        </div>

        <div className="products-grid">
          {resinFrames.map((frame) => (
            <div key={frame.id} className="product-card">
              <div className="product-image">
                <img src={frame.image} alt={frame.name} style={{height: '120px', width: 'auto', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)'}} />
              </div>
              <div className="product-info">
                <h3 className="product-name">{frame.name}</h3>
                <p className="product-description">{frame.description}</p>
                <div className="product-price">₹{frame.price}</div>
                <button 
                  onClick={() => addToCart({
                    name: frame.name,
                    price: frame.price,
                    description: frame.description,
                    category: 'Resin Frames',
                  })}
                  className="btn btn-primary add-to-cart-btn"
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="product-info-section">
          <h2>About Our Resin Frames</h2>
          <div className="info-grid">
            <div className="info-item">
              <h3>💎 Premium Resin</h3>
              <p>Made from high-quality resin materials that provide crystal-clear visibility and durability.</p>
            </div>
            <div className="info-item">
              <h3>🎨 Modern Design</h3>
              <p>Contemporary aesthetic that complements any home or office decor.</p>
            </div>
            <div className="info-item">
              <h3>🛡️ Protection</h3>
              <p>Excellent protection for your currency notes while maintaining perfect visibility.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResinFrames;
