import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import ScrollToTop from './components/ScrollToTop';
import Home from './pages/Home';
import About from './pages/About';
import Contact from './pages/Contact';
import Cart from './pages/Cart';
import Payment from './pages/Payment';
import Login from './pages/Login';
import CurrencyNotes from './pages/products/CurrencyNotes';
import FramedNotes from './pages/products/FramedNotes';
import ResinFrames from './pages/products/ResinFrames';
import ZodiacCoins from './pages/products/ZodiacCoins';
import './App.css';

function App() {
  const [cartItems, setCartItems] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(() => {
    // Check localStorage for token
    return !!localStorage.getItem('token');
  });

  useEffect(() => {
    setIsLoggedIn(!!localStorage.getItem('token'));
  }, []);

  const addToCart = (item) => {
    setCartItems(prev => [...prev, { ...item, id: Date.now() }]);
  };

  const removeFromCart = (id) => {
    setCartItems(prev => prev.filter(item => item.id !== id));
  };

  return (
    <Router>
      <div className="App">
        <Navbar 
          cartItems={cartItems} 
          isLoggedIn={isLoggedIn} 
          setIsLoggedIn={setIsLoggedIn}
        />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/products/currency-notes" element={<CurrencyNotes addToCart={addToCart} />} />
          <Route path="/products/framed-notes" element={<FramedNotes addToCart={addToCart} />} />
          <Route path="/products/resin-frames" element={<ResinFrames addToCart={addToCart} />} />
          <Route path="/products/zodiac-coins" element={<ZodiacCoins addToCart={addToCart} />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/cart" element={<Cart cartItems={cartItems} removeFromCart={removeFromCart} />} />
          <Route path="/payment" element={<Payment cartItems={cartItems} />} />
          <Route path="/login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
        </Routes>
        <ScrollToTop />
      </div>
    </Router>
  );
}

export default App;
