import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { orderAPI } from '../services/api';
import './Payment.css';

const Payment = () => {
  const { cartItems, cartTotals, clearCart } = useCart();
  const { user } = useAuth();
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [formData, setFormData] = useState({
    // Billing Info (pre-filled from user if available)
    fullName: user?.name || '',
    email: user?.email || '',
    phone: user?.phone || '',
    address: '',
    city: '',
    state: '',
    pincode: '',

    // Card Info
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardName: '',

    // UPI Info
    upiId: ''
  });

  const navigate = useNavigate();
  const maxDeliveryDays = Math.max(0, ...cartItems.map(i => (i.w_days ?? 5)));

  // Use cartTotals from CartContext instead of calculating here
  const total = cartTotals.total || 0;

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const cart_items = cartItems.map(item => {
        let datewith_instructions = '';
        if (item.customization) {
          const customizationParts = [];
          if (item.customization.specialData) {
            customizationParts.push(`Special Date: ${item.customization.specialData}`);
          }
          if (item.customization.names) {
            customizationParts.push(`Names: ${item.customization.names}`);
          }
          if (item.customization.description) {
            customizationParts.push(`Description: ${item.customization.description}`);
          }
          if (customizationParts.length > 0) {
            datewith_instructions = customizationParts.join(' | ');
          }
        }

        // Ensure datewith_instructions is never null
        if (datewith_instructions === null || datewith_instructions === undefined) {
          datewith_instructions = '';
        }

        const imgs = Array.isArray(item.customization?.imageUrls)
          ? item.customization.imageUrls.filter(Boolean)
          : (item.customization?.imageUrl ? [item.customization.imageUrl] : []);
        const folder = item.customization?.folder || '';

        return {
          product_name: item.id, // Corresponds to product's primary key
          quantity: item.quantity || 1,
          // Save the first image URL for display in orders (DB unchanged)
          custom_photo_url: (imgs.length > 0 ? imgs[0] : null),
          // Provide the pending folder so backend can move assets and save final folder path
          custom_photo_folder: folder || null,
          datewith_instructions
        };
      });

      const orderData = {
        customer_name: formData.fullName,
        customer_email: formData.email,
        customer_phone: formData.phone,
        shipping_address: `${formData.address}, ${formData.city}, ${formData.state} - ${formData.pincode}`,
        cart_items
      };

      const order = await orderAPI.create(orderData);

      clearCart();
      const nums = order.order_numbers || (order.order_number ? [order.order_number] : []);
      const msg = `Payment successful! Orders ${nums.join(', ')} placed. Ref: ${order.upi_reference_id}`;
      alert(msg);
      navigate('/account');

    } catch (error) {
      console.error('Order creation failed:', error);
      alert(`Order failed: ${error.message}`);
    }
  };

  if (cartItems.length === 0) {
    return (
      <div className="payment-page">
        <div className="container">
          <div className="empty-cart">
            <h2>No Items to Checkout</h2>
            <p>Your cart is empty. Please add items before proceeding to payment.</p>
            <button onClick={() => navigate('/')} className="btn btn-primary">
              Continue Shopping
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="payment-page">
      <div className="container">
        <div className="page-header">
          <h1 className="page-title">Checkout</h1>
          <p className="page-subtitle">Complete your purchase</p>
        </div>

        <div className="payment-content">
          <div className="payment-form-section">
            <form onSubmit={handleSubmit} className="payment-form">
              {/* Billing Information */}
              <div className="form-section">
                <h2>Billing Information</h2>
                <div className="form-grid">
                  <div className="form-group">
                    <label htmlFor="fullName">Full Name *</label>
                    <input
                      type="text"
                      id="fullName"
                      name="fullName"
                      value={formData.fullName}
                      onChange={handleChange}
                      required
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="email">Email Address *</label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="phone">Phone Number *</label>
                    <input
                      type="tel"
                      id="phone"
                      name="phone"
                      value={formData.phone}
                      onChange={handleChange}
                      required
                      className="form-input"
                    />
                  </div>

                  <div className="form-group full-width">
                    <label htmlFor="address">Address *</label>
                    <input
                      type="text"
                      id="address"
                      name="address"
                      value={formData.address}
                      onChange={handleChange}
                      required
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="city">City *</label>
                    <input
                      type="text"
                      id="city"
                      name="city"
                      value={formData.city}
                      onChange={handleChange}
                      required
                      className="form-input"
                    />
                  </div>

                  <div className="form-group">
                    <label htmlFor="state">State *</label>
                    <select
                      id="state"
                      name="state"
                      value={formData.state}
                      onChange={handleChange}
                      required
                      className="form-input"
                    >
                      <option value="">Select State</option>
                      <option value="Maharashtra">Maharashtra</option>
                      <option value="Delhi">Delhi</option>
                      <option value="Karnataka">Karnataka</option>
                      <option value="Tamil Nadu">Tamil Nadu</option>
                      <option value="Gujarat">Gujarat</option>
                      <option value="Rajasthan">Rajasthan</option>
                      <option value="West Bengal">West Bengal</option>
                      <option value="Uttar Pradesh">Uttar Pradesh</option>
                    </select>
                  </div>

                  <div className="form-group">
                    <label htmlFor="pincode">PIN Code *</label>
                    <input
                      type="text"
                      id="pincode"
                      name="pincode"
                      value={formData.pincode}
                      onChange={handleChange}
                      required
                      className="form-input"
                    />
                  </div>
                </div>
              </div>

              {/* Payment Method */}
              <div className="form-section">
                <h2>Payment Method</h2>
                <div className="payment-methods">
                  <label className={`payment-method ${paymentMethod === 'card' ? 'selected' : ''}`}>
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="card"
                      checked={paymentMethod === 'card'}
                      onChange={(e) => setPaymentMethod(e.target.value)}
                    />
                    <span className="method-icon">💳</span>
                    <span>Credit/Debit Card</span>
                  </label>

                  <label className={`payment-method ${paymentMethod === 'upi' ? 'selected' : ''}`}>
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="upi"
                      checked={paymentMethod === 'upi'}
                      onChange={(e) => setPaymentMethod(e.target.value)}
                    />
                    <span className="method-icon">📱</span>
                    <span>UPI</span>
                  </label>
                </div>

                {/* Card Payment Form */}
                {paymentMethod === 'card' && (
                  <div className="card-form">
                    <div className="form-grid">
                      <div className="form-group full-width">
                        <label htmlFor="cardNumber">Card Number *</label>
                        <input
                          type="text"
                          id="cardNumber"
                          name="cardNumber"
                          value={formData.cardNumber}
                          onChange={handleChange}
                          placeholder="1234 5678 9012 3456"
                          required
                          className="form-input"
                        />
                      </div>

                      <div className="form-group">
                        <label htmlFor="expiryDate">Expiry Date *</label>
                        <input
                          type="text"
                          id="expiryDate"
                          name="expiryDate"
                          value={formData.expiryDate}
                          onChange={handleChange}
                          placeholder="MM/YY"
                          required
                          className="form-input"
                        />
                      </div>

                      <div className="form-group">
                        <label htmlFor="cvv">CVV *</label>
                        <input
                          type="text"
                          id="cvv"
                          name="cvv"
                          value={formData.cvv}
                          onChange={handleChange}
                          placeholder="123"
                          required
                          className="form-input"
                        />
                      </div>

                      <div className="form-group full-width">
                        <label htmlFor="cardName">Name on Card *</label>
                        <input
                          type="text"
                          id="cardName"
                          name="cardName"
                          value={formData.cardName}
                          onChange={handleChange}
                          required
                          className="form-input"
                        />
                      </div>
                    </div>
                  </div>
                )}

                {/* UPI Payment Form */}
                {paymentMethod === 'upi' && (
                  <div className="upi-form">
                    <div className="form-group">
                      <label htmlFor="upiId">UPI ID *</label>
                      <input
                        type="text"
                        id="upiId"
                        name="upiId"
                        value={formData.upiId}
                        onChange={handleChange}
                        placeholder="yourname@paytm"
                        required
                        className="form-input"
                      />
                    </div>
                  </div>
                )}


              </div>

              <button type="submit" className="btn btn-primary place-order-btn">
                Place Order - ₹{total}
              </button>
              <div style={{marginTop:'8px', color:'#444', fontSize:'0.95rem'}}>
                Estimated delivery in <strong>{maxDeliveryDays}</strong> days
              </div>
            </form>
          </div>

          {/* Order Summary */}
          <div className="order-summary">
            <div className="summary-card">
              <h2>Order Summary</h2>

              <div className="order-items">
                {cartItems.map((item) => (
                  <div key={item.id} className="order-item">
                    <div className="item-details">
                      <h4>{item.name}</h4>
                      <p>{item.category}</p>
                      {(() => {
                        const c = item.customization || {};
                        const imgs = Array.isArray(c.imageUrls) ? c.imageUrls : (c.imageUrl ? [c.imageUrl] : []);
                        const imageNames = (Array.isArray(c.imageNames) && c.imageNames.length > 0)
                          ? c.imageNames
                          : imgs.map((u) => {
                              try {
                                const last = (u || '').split('/').pop() || '';
                                return decodeURIComponent((last.split('?')[0]) || last);
                              } catch { return u; }
                            }).filter(Boolean);
                        if (!c.specialData && !c.description && imageNames.length === 0 && imgs.length === 0) return null;
                        return (
                          <div className="item-customization" style={{marginTop:'6px', fontSize:'0.9rem', color:'#333'}}>
                            {c.specialData && <div><strong>Special Date:</strong> {c.specialData}</div>}
                            {c.description && <div><strong>Description:</strong> {c.description}</div>}
                            {imageNames.length > 0 && (
                              <div style={{marginTop:'4px'}}><strong>Images:</strong> {imageNames.join(', ')}</div>
                            )}
                            {imgs.length > 0 && (
                              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(50px, 1fr))', gap: '6px', marginTop: '6px' }}>
                                {imgs.map((src, idx) => (
                                  <img key={idx} src={src} alt={`Img ${idx+1}`} style={{ width: '100%', height: '50px', objectFit: 'cover', borderRadius: '4px', border:'1px solid #eee' }} />
                                ))}
                              </div>
                            )}
                          </div>
                        );
                      })()}
                    </div>
                    <div className="item-price">₹{item.price}</div>
                  </div>
                ))}
              </div>

              <hr className="summary-divider" />

              <div className="summary-row">
                <span>Subtotal ({cartItems.length} items)</span>
                <span>₹{cartTotals.subtotal}</span>
              </div>

              <div className="summary-row">
                <span>Shipping</span>
                <span>₹{cartTotals.shipping}</span>
              </div>

              <div className="summary-row">
                <span>Tax (GST 18%)</span>
                <span>₹{cartTotals.gst}</span>
              </div>

              <div className="summary-row">
                <span>Estimated Delivery</span>
                <span>{maxDeliveryDays} days</span>
              </div>

              <hr className="summary-divider" />

              <div className="summary-row total-row">
                <span>Total</span>
                <span>₹{total}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Payment;
