import React from 'react';
import './ProductPages.css';

const CurrencyNotes = ({ addToCart }) => {
  const currencyNotes = [
    { denomination: '₹1', price: 50, description: 'Special serial number ₹1 note', image: '/assets/Currency_notes/1.jpg' },
    { denomination: '₹5', price: 75, description: 'Special serial number ₹5 note', image: '/assets/Currency_notes/5.jpeg' },
    { denomination: '₹10', price: 100, description: 'Special serial number ₹10 note', image: '/assets/Currency_notes/10.jpg' },
    { denomination: '₹20', price: 150, description: 'Special serial number ₹20 note', image: '/assets/Currency_notes/20.jpg' },
    { denomination: '₹50', price: 200, description: 'Special serial number ₹50 note', image: '/assets/Currency_notes/50.jpg' },
    { denomination: '₹100', price: 300, description: 'Special serial number ₹100 note', image: '/assets/Currency_notes/100.jpg' },
    { denomination: '₹200', price: 500, description: 'Special serial number ₹200 note', image: '/assets/Currency_notes/200.jpg' },
    { denomination: '₹500', price: 800, description: 'Special serial number ₹500 note', image: '/assets/Currency_notes/500.jpg' }
  ];

  const handleAddToCart = (note) => {
    addToCart({
      name: `${note.denomination} Currency Note`,
      price: note.price,
      description: note.description,
      category: 'Currency Notes'
    });
    alert(`${note.denomination} note added to cart!`);
  };

  return (
    <div className="product-page">
      <div className="container">
        <div className="page-header">
          <h1 className="page-title">Currency Notes</h1>
          <p className="page-subtitle">
            Special serial number currency notes perfect for gifting on birthdays, anniversaries, and special occasions
          </p>
        </div>

        <div className="products-grid">
          {currencyNotes.map((note, index) => (
            <div key={index} className="product-card">
              <div className="product-image">
                <img src={note.image} alt={note.denomination + ' currency note'} style={{height: '100px', width: 'auto', borderRadius: '6px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)'}} />
              </div>

              <div className="product-info">
                <h3 className="product-name">{note.denomination} Currency Note</h3>
                <p className="product-description">{note.description}</p>
                <div className="product-price">₹{note.price}</div>

                <button 
                  onClick={() => handleAddToCart(note)}
                  className="btn btn-primary add-to-cart-btn"
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="product-info-section">
          <h2>About Our Currency Notes</h2>
          <div className="info-grid">
            <div className="info-item">
              <h3>🎯 Special Serial Numbers</h3>
              <p>We carefully select currency notes with serial numbers matching special dates like birthdays, anniversaries, or lucky numbers.</p>
            </div>
            <div className="info-item">
              <h3>✨ Authentic & Genuine</h3>
              <p>All our currency notes are authentic and in excellent condition, perfect for gifting and collecting.</p>
            </div>
            <div className="info-item">
              <h3>🎁 Perfect Gifts</h3>
              <p>Unique and meaningful gifts that create lasting memories for your loved ones.</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CurrencyNotes;
