import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ cartItems, isLoggedIn, setIsLoggedIn, darkMode, toggleDarkMode }) => {
  const [isProductsOpen, setIsProductsOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    setIsLoggedIn(false);
    navigate('/');
  };

  const productCategories = [
    { name: 'Currency Notes', path: '/products/currency-notes', icon: 'üíµ' },
    { name: 'Framed Notes', path: '/products/framed-notes', icon: 'üñºÔ∏è' },
    { name: 'Resin Frames', path: '/products/resin-frames', icon: '‚ú®' },
    { name: 'Zodiac Coins', path: '/products/zodiac-coins', icon: 'ü™ô' }
  ];

  return (
    <nav className="navbar">
      <div className="container">
        <div className="nav-content">
          <Link to="/" className="nav-logo">
            <h2>Frames & More</h2>
          </Link>

          <div className="nav-links">
            <Link to="/" className="nav-link">Home</Link>

            <div 
              className="products-dropdown"
              onMouseEnter={() => setIsProductsOpen(true)}
              onMouseLeave={() => setIsProductsOpen(false)}
            >
              <span className="nav-link products-trigger">
                Products ‚ñº
              </span>

              {isProductsOpen && (
                <div className="dropdown-menu">
                  {productCategories.map((category, index) => (
                    <Link
                      key={index}
                      to={category.path}
                      className="dropdown-item"
                      onClick={() => setIsProductsOpen(false)}
                    >
                      <span className="dropdown-icon">{category.icon}</span>
                      <span>{category.name}</span>
                    </Link>
                  ))}
                </div>
              )}
            </div>

            <Link to="/about" className="nav-link">About</Link>
            <Link to="/contact" className="nav-link">Contact</Link>
          </div>

          <div className="nav-actions">
            <button 
              onClick={toggleDarkMode} 
              className="dark-mode-toggle"
              aria-label="Toggle dark mode"
            >
              {darkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>

            {isLoggedIn ? (
              <button onClick={handleLogout} className="nav-link logout-btn">
                Logout
              </button>
            ) : (
              <Link to="/login" className="nav-link">Login</Link>
            )}

            <Link to="/cart" className="cart-link">
              <div className="cart-icon">
                üõí
                {cartItems.length > 0 && (
                  <span className="cart-count">{cartItems.length}</span>
                )}
              </div>
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
