import React, { createContext, useContext, useState, useEffect } from 'react';
import { cartAPI } from '../services/api';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [cartTotals, setCartTotals] = useState({
    subtotal: 0,
    gst: 0,
    shipping: 0,
    total: 0
  });
  const [loading, setLoading] = useState(false);

  // Load cart from localStorage on component mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      try {
        const parsedCart = JSON.parse(savedCart);
        // Normalize legacy items to ensure expected customization fields exist
        const normalized = Array.isArray(parsedCart) ? parsedCart.map((it) => {
          const c = it?.customization || {};
          const imageUrls = Array.isArray(c.imageUrls)
            ? c.imageUrls
            : (c.imageUrl ? [c.imageUrl] : []);
          const imageUrl = imageUrls[0] || c.imageUrl || '';
          return {
            ...it,
            customization: {
              // Common
              specialData: c.specialData || '',
              names: c.names || '',
              description: c.description || '',
              imageUrl,
              imageUrls,
              imageNames: Array.isArray(c.imageNames) ? c.imageNames : [],
              folder: c.folder || '',
              priceBreakdown: c.priceBreakdown || null,
              // Resin-specific
              resinDate1: c.resinDate1 || '',
              resinDate2: c.resinDate2 || '',
              resinName1: c.resinName1 || '',
              resinName2: c.resinName2 || '',
              resinEvent: c.resinEvent || '',
              resinEventDate: c.resinEventDate || c.specialData || '',
              resinEventOther: c.resinEventOther || '',
              // Frame-specific (persist for big/custom frames)
              frameName1: c.frameName1 || '',
              frameName2: c.frameName2 || '',
              frameDate1: c.frameDate1 || '',
              frameDate2: c.frameDate2 || '',
              frameEvent: c.frameEvent || '',
              frameEventDate: c.frameEventDate || '',
              frameEventOther: c.frameEventOther || '',
              frameSetType: c.frameSetType || '',
              // Event aliases sometimes used in UI
              event: c.event || '',
              customEvent: c.customEvent || '',
              // Custom frame fields
              customNames: Array.isArray(c.customNames) ? c.customNames : [],
              customNotes: Array.isArray(c.customNotes) ? c.customNotes : []
            }
          };
        }) : [];
        setCartItems(normalized);
      } catch (error) {
        console.error('Error parsing cart from localStorage:', error);
        localStorage.removeItem('cart');
      }
    }
  }, []);

  // Save cart to localStorage whenever cartItems changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
    if (cartItems.length > 0) {
      calculateCartTotals();
    } else {
      setCartTotals({ subtotal: 0, gst: 0, shipping: 0, total: 0 });
    }
  }, [cartItems]);

  const calculateCartTotals = () => {
    if (cartItems.length === 0) return;

    // Calculate totals locally since we don't have pricing in database
    const subtotal = cartItems.reduce((sum, item) => sum + (item.price * (item.quantity || 1)), 0);
    const gst = 0; // No GST
    const shipping = subtotal > 999 ? 0 : 68;
    const total = subtotal + shipping; // gst is 0

    setCartTotals({
      subtotal,
      gst,
      shipping,
      total
    });
  };

  const addToCart = (product, quantity = 1, customization = {}) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => 
        item.id === product.id && 
        JSON.stringify(item.customization || {}) === JSON.stringify(customization)
      );

      if (existingItem) {
        // Update quantity if item already exists with same customization
        return prevItems.map(item =>
          item.id === product.id && 
          JSON.stringify(item.customization || {}) === JSON.stringify(customization)
            ? { ...item, quantity: (item.quantity || 1) + quantity }
            : item
        );
      } else {
        // Add new item to cart with customization
        return [...prevItems, { 
          ...product, 
          quantity,
          addedAt: Date.now(),
          customization: {
            // Common
            specialData: customization.specialData || '',
            names: customization.names || '',
            description: customization.description || '',
            imageUrl: (customization.imageUrls && customization.imageUrls[0]) || customization.imageUrl || '',
            imageUrls: Array.isArray(customization.imageUrls) ? customization.imageUrls : (customization.imageUrl ? [customization.imageUrl] : []),
            imageNames: Array.isArray(customization.imageNames) ? customization.imageNames : [],
            folder: customization.folder || '',
            priceBreakdown: customization.priceBreakdown || null,
            // Resin-specific
            resinDate1: customization.resinDate1 || '',
            resinDate2: customization.resinDate2 || '',
            resinName1: customization.resinName1 || '',
            resinName2: customization.resinName2 || '',
            resinEvent: customization.resinEvent || '',
            resinEventDate: customization.resinEventDate || '',
            resinEventOther: customization.resinEventOther || '',
            // Frame-specific
            frameName1: customization.frameName1 || '',
            frameName2: customization.frameName2 || '',
            frameDate1: customization.frameDate1 || '',
            frameDate2: customization.frameDate2 || '',
            frameEvent: customization.frameEvent || '',
            frameEventDate: customization.frameEventDate || '',
            frameEventOther: customization.frameEventOther || '',
            frameSetType: customization.frameSetType || '',
            // Event aliases used in UI
            event: customization.event || '',
            customEvent: customization.customEvent || '',
            // Custom frame arrays
            customNames: Array.isArray(customization.customNames) ? customization.customNames : [],
            customNotes: Array.isArray(customization.customNotes) ? customization.customNotes : []
          }
        }];
      }
    });

    console.log(`${product.name} added to cart with customization!`);
  };

  const removeFromCart = (productId) => {
    setCartItems(prevItems => {
      const itemToRemove = prevItems.find(item => item.id === productId);
      if (itemToRemove) {
        console.log(`${itemToRemove.name} removed from cart`);
      }
      return prevItems.filter(item => item.id !== productId);
    });
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
      return;
    }

    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === productId
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  const clearCart = () => {
    setCartItems([]);
    localStorage.removeItem('cart');
    console.log('Cart cleared');
  };

  const getCartItemsCount = () => {
    return cartItems.reduce((total, item) => total + (item.quantity || 1), 0);
  };

  const checkStockAvailability = async () => {
    // Since all products are always available, return success
    return { 
      allItemsAvailable: true, 
      items: cartItems.map(item => ({
        productId: item.id,
        available: true,
        requestedQuantity: item.quantity || 1,
        availableQuantity: 999
      }))
    };
  };

  const value = {
    cartItems,
    cartTotals,
    loading,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartItemsCount,
    checkStockAvailability,
    calculateCartTotals
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
